import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useLanguage } from '../context/LanguageContext';

export default function Navigation() {
  const { user, logout, requireAuth } = useAuth();
  const { language, changeLanguage, t } = useLanguage();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav>
      <div className="nav-container">
        <Link to="/" className="logo">üèõÔ∏è Raja Rajeshwara</Link>
        <ul>
          <li><Link to="/">{t('nav_home')}</Link></li>
          <li><Link to="/events">{t('nav_events')}</Link></li>
          <li><Link to="/services">{t('nav_services')}</Link></li>
          <li><Link to="/staff">üßë‚Äçüíº Staff</Link></li>
          <li><Link to="/timings">{t('nav_timings')}</Link></li>
          <li><Link to="/board-members">üë• Board</Link></li>
          <li><Link to="/admin">‚öôÔ∏è Admin</Link></li>
        </ul>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {/* Language Selector */}
          <select 
            value={language} 
            onChange={(e) => changeLanguage(e.target.value)}
            style={{
              background: 'rgba(255,255,255,0.2)',
              color: '#fff',
              border: '1px solid rgba(255,255,255,0.3)',
              padding: '0.5rem',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '0.9rem'
            }}
          >
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          {user && (
            <>
              <span style={{ color: '#fff', fontSize: '0.9rem' }}>
                üë§ {user.name || user.email}
              </span>
              <button
                onClick={handleLogout}
                style={{
                  background: 'rgba(255,255,255,0.2)',
                  color: '#fff',
                  border: '1px solid rgba(255,255,255,0.3)',
                  padding: '0.5rem 1rem',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '0.9rem'
                }}
              >
                {t('nav_logout')}
              </button>
            </>
          )}
          {requireAuth && !user && (
            <Link to="/login" style={{ color: '#ffd700', textDecoration: 'none', fontWeight: 'bold' }}>
              Login
            </Link>
          )}
          <span style={{ color: '#fff', fontSize: '0.8rem', opacity: 0.7, marginLeft: '1rem' }}>
            Env: {requireAuth ? 'AUTH' : 'NO-AUTH'}
          </span>
        </div>
      </div>
    </nav>
  );
}
