import React from 'react';
import { useLanguage } from '../context/LanguageContext';
import IndiaTime from './IndiaTime';

export default function Footer() {
  const { t } = useLanguage();

  return (
    <>
      <IndiaTime />
      <footer>
        <p>&copy; 2025 Raja Rajeshwara Devastanam. {t('footer_rights')}.</p>
        <p>üìç {t('footer_address')}: Near Old Market, Hasaparthy, Warangal | üìû {t('footer_phone')}: (555) 123-4567</p>
        <p>{t('footer_email')}: info@rajarajeshwara.com</p>
      </footer>
    </>
  );
}
