// Environment configuration
const ENV = process.env.REACT_APP_ENV || 'local';

const config = {
  local: {
    env: 'local',
    apiUrl: 'http://localhost:3001',
    backendUrl: 'http://localhost:8080',
    requireAuth: false,
    authProvider: null
  },
  dev: {
    env: 'dev',
    apiUrl: 'https://dev-api.temple.com',
    backendUrl: 'https://dev-backend-api.temple.com',
    requireAuth: true,
    authProvider: 'oauth2'
  },
  uat: {
    env: 'uat',
    apiUrl: 'https://uat-api.temple.com',
    backendUrl: 'https://uat-backend-api.temple.com',
    requireAuth: true,
    authProvider: 'oauth2'
  },
  production: {
    env: 'production',
    apiUrl: 'https://api.temple.com',
    backendUrl: 'https://api.temple.com',
    requireAuth: true,
    authProvider: 'oauth2'
  }
};

export default config[ENV];
