import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { LanguageProvider } from './context/LanguageContext';
import Navigation from './components/Navigation';
import Footer from './components/Footer';
import IndiaTime from './components/IndiaTime';
import ProtectedRoute from './components/ProtectedRoute';
import Login from './pages/Login';
import UserHome from './pages/UserHome';
import AdminDashboard from './pages/AdminDashboard';
import EventsUser from './pages/EventsUser';
import EventsAdmin from './pages/EventsAdmin';
import Services from './pages/Services';
import Staff from './pages/Staff';
import Timings from './pages/Timings';
import Donors from './pages/Donors';
import BoardMembers from './pages/BoardMembers';
import './styles/App.css';

function AppRoutes() {
  return (
    <>
      <Navigation />
      <Routes>
        {/* Public Routes */}
        <Route path="/login" element={<Login />} />

        {/* USER ROUTES - Read-only pages */}
        <Route path="/" element={
          <ProtectedRoute>
            <UserHome />
          </ProtectedRoute>
        } />
        <Route path="/events" element={
          <ProtectedRoute>
            <EventsUser />
          </ProtectedRoute>
        } />
        <Route path="/services" element={
          <ProtectedRoute>
            <Services />
          </ProtectedRoute>
        } />
        <Route path="/staff" element={
          <ProtectedRoute>
            <Staff />
          </ProtectedRoute>
        } />
        <Route path="/timings" element={
          <ProtectedRoute>
            <Timings />
          </ProtectedRoute>
        } />
        <Route path="/donors" element={
          <ProtectedRoute>
            <Donors />
          </ProtectedRoute>
        } />
        <Route path="/board-members" element={
          <ProtectedRoute>
            <BoardMembers />
          </ProtectedRoute>
        } />

        {/* ADMIN ROUTES - Management pages */}
        <Route path="/events/admin" element={
          <ProtectedRoute requireAdmin>
            <EventsAdmin />
          </ProtectedRoute>
        } />
        <Route path="/admin/dashboard" element={
          <ProtectedRoute requireAdmin>
            <AdminDashboard />
          </ProtectedRoute>
        } />

        {/* Fallback route */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
      <Footer />
    </>
  );
}

export default function App() {
  return (
    <BrowserRouter>
      <LanguageProvider>
        <AuthProvider>
          <IndiaTime />
          <AppRoutes />
        </AuthProvider>
      </LanguageProvider>
    </BrowserRouter>
  );
}
