import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useLanguage } from '../context/LanguageContext';

export default function Navigation() {
  const { user, logout, requireAuth } = useAuth();
  const { language, changeLanguage, t } = useLanguage();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav>
      <div className="nav-container">
        <Link to="/" className="logo">üèõÔ∏è Raja Rajeshwara</Link>
        <ul>
          <li><Link to="/">{t('nav_home')}</Link></li>
          <li><Link to="/events">{t('nav_events')}</Link></li>
          <li><Link to="/services">{t('nav_services')}</Link></li>
          <li><Link to="/staff">üßë‚Äçüíº Staff</Link></li>
          <li><Link to="/timings">{t('nav_timings')}</Link></li>
          <li><Link to="/board-members">üë• Board</Link></li>
          {user?.role === 'admin' && <li><Link to="/admin/dashboard">‚öôÔ∏è Admin Dashboard</Link></li>}
        </ul>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {/* Language Selector */}
          <select 
            value={language} 
            onChange={(e) => changeLanguage(e.target.value)}
            style={{
              background: 'rgba(230, 179, 37, 0.15)',
              color: '#E6B325',
              border: '1px solid #E6B325',
              padding: '0.5rem',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '0.9rem',
              fontWeight: '500'
            }}
          >
            <option value="en" style={{ color: '#333' }}>English</option>
            <option value="te" style={{ color: '#333' }}>‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi" style={{ color: '#333' }}>‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          {user && (
            <>
              <span style={{ color: '#E6B325', fontSize: '0.9rem', fontWeight: '500' }}>
                üë§ {user.name || user.email}
              </span>
              <button
                onClick={handleLogout}
                style={{
                  background: 'rgba(230, 179, 37, 0.15)',
                  color: '#E6B325',
                  border: '1px solid #E6B325',
                  padding: '0.5rem 1rem',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '500',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = '#E6B325';
                  e.target.style.color = '#0B1C3F';
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = 'rgba(230, 179, 37, 0.15)';
                  e.target.style.color = '#E6B325';
                }}
              >
                {t('nav_logout')}
              </button>
            </>
          )}
          {requireAuth && !user && (
            <Link to="/login" style={{ color: '#E6B325', textDecoration: 'none', fontWeight: 'bold', fontSize: '0.95rem' }}>
              Login
            </Link>
          )}
          <span style={{ color: '#b0c4de', fontSize: '0.8rem', opacity: 0.8, marginLeft: '1rem' }}>
            Env: {requireAuth ? 'AUTH' : 'NO-AUTH'}
          </span>
        </div>
      </div>
    </nav>
  );
}
