import { useState } from 'react';
import { useLanguage } from '../context/LanguageContext';
import { useAuth } from '../context/AuthContext';
import { useUpcomingEvents } from '../hooks/useAPI';
import { eventsAPI } from '../services/api';

export default function Events() {
  const { t } = useLanguage();
  const { isAuthenticated } = useAuth();
  const { events: backendEvents, loading, error, refetch } = useUpcomingEvents();
  const [showAddForm, setShowAddForm] = useState(false);
  const [formData, setFormData] = useState({
    eventName: '',
    description: '',
    eventDate: '',
    startTime: '',
    endTime: '',
    location: '',
    category: 'Festival'
  });
  const [submitLoading, setSubmitLoading] = useState(false);
  const [submitError, setSubmitError] = useState(null);

  // No fallback mock data - only show backend data
  const displayEvents = backendEvents && backendEvents.length > 0 ? backendEvents : [];

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });
  };

  const formatTime = (timeString) => {
    if (!timeString) return '';
    const parts = timeString.split(':');
    const hours = parseInt(parts[0]);
    const minutes = parts[1] || '00';
    const ampm = hours >= 12 ? 'PM' : 'AM';
    const displayHour = hours % 12 || 12;
    return `${displayHour}:${minutes} ${ampm}`;
  };

  const handleAddEvent = async (e) => {
    e.preventDefault();
    setSubmitLoading(true);
    setSubmitError(null);

    try {
      await eventsAPI.create(formData);
      setFormData({
        eventName: '',
        description: '',
        eventDate: '',
        startTime: '',
        endTime: '',
        location: '',
        category: 'Festival'
      });
      setShowAddForm(false);
      refetch();
    } catch (err) {
      setSubmitError(err.response?.data?.message || 'Failed to add event');
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    if (!window.confirm('Are you sure you want to delete this event?')) return;

    try {
      await eventsAPI.delete(eventId);
      refetch();
    } catch (err) {
      alert('Failed to delete event: ' + (err.response?.data?.message || err.message));
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="page">
      <div className="hero" style={{
        backgroundImage: 'url(/images/temple-images/sitarama.webp)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        position: 'relative'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.5)'
        }}></div>
        <div style={{ position: 'relative', zIndex: 1, textAlign: 'center' }}>
          <h1 style={{ color: 'white' }}>üìÖ {t('events_title')}</h1>
          <p style={{ color: '#f0f0f0' }}>{t('events_upcoming')}</p>
        </div>
      </div>

      <div className="container">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <h2 style={{ textAlign: 'center', flex: 1, color: '#8b4513' }}>{t('events_upcoming')}</h2>
          {isAuthenticated && (
            <button 
              className="btn" 
              onClick={() => setShowAddForm(!showAddForm)}
              style={{ marginLeft: 'auto' }}
            >
              {showAddForm ? '‚ùå Cancel' : '‚ûï Add Event'}
            </button>
          )}
        </div>

        {showAddForm && isAuthenticated && (
          <div style={{
            background: '#f9f9f9',
            padding: '2rem',
            borderRadius: '8px',
            marginBottom: '2rem',
            border: '1px solid #ddd'
          }}>
            <h3 style={{ color: '#8b4513', marginBottom: '1.5rem' }}>Add New Event</h3>
            {submitError && (
              <div style={{ background: '#ffe5e5', padding: '1rem', borderRadius: '4px', color: '#d32f2f', marginBottom: '1rem' }}>
                ‚ö†Ô∏è {submitError}
              </div>
            )}
            <form onSubmit={handleAddEvent}>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
                <input
                  type="text"
                  name="eventName"
                  placeholder="Event Name"
                  value={formData.eventName}
                  onChange={handleInputChange}
                  required
                  style={{ padding: '0.75rem', borderRadius: '4px', border: '1px solid #ddd' }}
                />
                <input
                  type="date"
                  name="eventDate"
                  value={formData.eventDate}
                  onChange={handleInputChange}
                  required
                  style={{ padding: '0.75rem', borderRadius: '4px', border: '1px solid #ddd' }}
                />
              </div>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
                <input
                  type="time"
                  name="startTime"
                  value={formData.startTime}
                  onChange={handleInputChange}
                  required
                  style={{ padding: '0.75rem', borderRadius: '4px', border: '1px solid #ddd' }}
                />
                <input
                  type="time"
                  name="endTime"
                  value={formData.endTime}
                  onChange={handleInputChange}
                  required
                  style={{ padding: '0.75rem', borderRadius: '4px', border: '1px solid #ddd' }}
                />
              </div>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
                <input
                  type="text"
                  name="location"
                  placeholder="Location"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                  style={{ padding: '0.75rem', borderRadius: '4px', border: '1px solid #ddd' }}
                />
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                  style={{ padding: '0.75rem', borderRadius: '4px', border: '1px solid #ddd' }}
                >
                  <option value="Festival">Festival</option>
                  <option value="Prayer">Prayer</option>
                  <option value="Ceremony">Ceremony</option>
                  <option value="Class">Class</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <textarea
                name="description"
                placeholder="Event Description"
                value={formData.description}
                onChange={handleInputChange}
                required
                rows="4"
                style={{ 
                  width: '100%', 
                  padding: '0.75rem', 
                  borderRadius: '4px', 
                  border: '1px solid #ddd',
                  marginBottom: '1rem',
                  fontFamily: 'inherit'
                }}
              />
              <button 
                type="submit" 
                className="btn"
                disabled={submitLoading}
                style={{ opacity: submitLoading ? 0.6 : 1, cursor: submitLoading ? 'not-allowed' : 'pointer' }}
              >
                {submitLoading ? 'Adding...' : 'Add Event'}
              </button>
            </form>
          </div>
        )}

        {loading && (
          <div style={{ textAlign: 'center', padding: '2rem', color: '#666' }}>
            Loading events...
          </div>
        )}

        {error && (
          <div style={{ background: '#ffe5e5', padding: '1rem', borderRadius: '4px', color: '#d32f2f', marginBottom: '2rem' }}>
            ‚ö†Ô∏è {error}
          </div>
        )}

        <div className="cards-grid">
          {displayEvents.map(event => (
            <div key={event.id} className="card">
              <div className="card-image" style={{ fontSize: '2.5rem', padding: '1rem', textAlign: 'center', background: '#f5e6d3' }}>
                {event.icon || 'üìÖ'}
              </div>
              <div className="card-content">
                <h3>{event.eventName}</h3>
                <p><strong>üìÖ Date:</strong> {formatDate(event.eventDate)}</p>
                <p><strong>‚è∞ Time:</strong> {formatTime(event.startTime)} - {formatTime(event.endTime)}</p>
                {event.location && <p><strong>üìç Location:</strong> {event.location}</p>}
                {event.category && <p><strong>Category:</strong> {event.category}</p>}
                <p style={{ margin: '1rem 0', color: '#555' }}>{event.description}</p>
                <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center', justifyContent: 'space-between' }}>
                  <span style={{ 
                    display: 'inline-block', 
                    padding: '0.25rem 0.75rem', 
                    background: event.status === 'Upcoming' ? '#4caf50' : '#ff9800',
                    color: 'white',
                    borderRadius: '12px',
                    fontSize: '0.85rem'
                  }}>
                    {event.status || 'Upcoming'}
                  </span>
                  {isAuthenticated && (
                    <button 
                      onClick={() => handleDeleteEvent(event.id)}
                      style={{
                        background: '#d32f2f',
                        color: 'white',
                        border: 'none',
                        padding: '0.5rem 1rem',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '0.85rem'
                      }}
                    >
                      üóëÔ∏è Delete
                    </button>
                  )}
                </div>
                <button className="btn" style={{ marginTop: '1rem', width: '100%' }}>Register</button>
              </div>
            </div>
          ))}
        </div>

        {displayEvents.length === 0 && !loading && (
          <div style={{ textAlign: 'center', padding: '3rem 1rem', color: '#999' }}>
            No events scheduled at the moment.
          </div>
        )}
      </div>
    </div>
  );
}
