import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';

export default function Home() {
  const navigate = useNavigate();
  const { t } = useLanguage();
  const [aboutTab, setAboutTab] = useState('history');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Array of Shiva images
  const shivaImages = [
    '/images/shiva/shiva img1.webp',
    '/images/shiva/shiva img 2.jpg',
    '/images/shiva/shiva img3.jpg',
    '/images/shiva/shiva img4.jpg',
    '/images/shiva/shiva img5.webp',
    '/images/shiva/shiva img 6.jpg',
    '/images/shiva/shiva img 7.png'
  ];

  // Auto-rotate images every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % shivaImages.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [shivaImages.length]);

  const aboutTabs = {
    history: 'Our temple has a rich history spanning centuries. We are dedicated to preserving traditions and serving the community with devotion and excellence.',
    community: 'We foster a vibrant community that celebrates cultural values, organizes spiritual programs, and supports the needs of our devotees.',
    synagogue: 'Our sacred synagogue is an architectural marvel, built with intricate designs and spiritual significance. It remains a place of peace and meditation.',
    awards: 'Over the decades, our temple has received numerous accolades for its contributions to society and spiritual guidance.'
  };

  return (
    <div className="page">
      {/* HERO SECTION */}
      <div className="hero" style={{
        backgroundImage: 'url(/images/temple-images/temple-main.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        position: 'relative',
        minHeight: '500px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-start'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.4)'
        }}></div>
        
        <div style={{ 
          position: 'relative', 
          zIndex: 1, 
          paddingLeft: '4rem',
          maxWidth: '600px'
        }}>
          <h1 style={{ 
            color: 'white', 
            fontSize: '3.5rem',
            fontWeight: 'bold',
            lineHeight: '1.2',
            marginBottom: '1.5rem'
          }}>
            God invites to the Home<br/>Of Peace
          </h1>
          <p style={{ 
            color: '#e0e0e0', 
            fontSize: '1rem',
            lineHeight: '1.8',
            marginBottom: '2rem',
            fontFamily: 'Georgia, serif'
          }}>
            ‡∞ì‡∞Ç ‡∞®‡∞Æ‡∞É ‡∞∂‡∞ø‡∞µ‡∞æ‡∞Ø<br/>
            ‡∞ì‡∞Ç ‡∞§‡±ç‡∞∞‡±ç‡∞Ø‡∞Ç‡∞¨‡∞ï‡∞Ç ‡∞Ø‡∞ú‡∞æ‡∞Æ‡∞π‡±á ‡∞∏‡±Å‡∞ó‡∞Ç‡∞ß‡∞ø‡∞Ç ‡∞™‡±Å‡∞∑‡±ç‡∞ü‡∞ø‡∞µ‡∞∞‡±ç‡∞ß‡∞®‡∞Ç<br/>
            ‡∞â‡∞∞‡±ç‡∞µ‡∞æ‡∞∞‡±Å‡∞ï‡∞Æ‡∞ø‡∞µ ‡∞¨‡∞Ç‡∞ß‡∞®‡∞æ‡∞®‡±ç ‡∞Æ‡±É‡∞§‡±ç‡∞Ø‡±ã‡∞∞‡±ç‡∞Æ‡±Å‡∞ï‡±ç‡∞∑‡±Ä‡∞Ø ‡∞Æ‡∞æ‡∞Æ‡±É‡∞§‡∞æ‡∞§‡±ç ‡••
          </p>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button 
              className="btn" 
              onClick={() => navigate('/events')}
              style={{
                background: '#f39c12',
                color: '#fff',
                padding: '0.8rem 2rem',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontWeight: 'bold',
                fontSize: '0.95rem'
              }}
            >
              JOIN NOW
            </button>
            <button 
              className="btn"
              onClick={() => navigate('/donors')}
              style={{
                background: '#f39c12',
                color: '#fff',
                padding: '0.8rem 2rem',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontWeight: 'bold',
                fontSize: '0.95rem'
              }}
            >
              DONATE NOW
            </button>
          </div>
        </div>
      </div>

      {/* ABOUT US SECTION */}
      <div className="container" style={{ paddingTop: '4rem', paddingBottom: '4rem' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '3rem', alignItems: 'start' }}>
          
          {/* LEFT SIDE - IMAGES */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div style={{
              backgroundImage: 'url(/images/temple-images/saibaba.jpg)',
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              height: '200px',
              borderRadius: '8px',
              boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
            }}></div>
            <div style={{
              backgroundImage: 'url(/images/temple-images/sitarama.webp)',
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              height: '200px',
              borderRadius: '8px',
              boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
            }}></div>
          </div>

          {/* RIGHT SIDE - ABOUT CONTENT */}
          <div>
            <h2 style={{
              fontSize: '2rem',
              color: '#333',
              marginBottom: '1.5rem',
              fontWeight: 'bold'
            }}>
              About Us
            </h2>

            {/* TABS */}
            <div style={{ 
              display: 'flex', 
              gap: '1rem', 
              marginBottom: '2rem',
              borderBottom: '2px solid #e0e0e0',
              paddingBottom: '1rem'
            }}>
              {Object.keys(aboutTabs).map(tab => (
                <button
                  key={tab}
                  onClick={() => setAboutTab(tab)}
                  style={{
                    padding: '0.5rem 1rem',
                    border: 'none',
                    background: 'transparent',
                    cursor: 'pointer',
                    fontSize: '0.95rem',
                    fontWeight: aboutTab === tab ? 'bold' : 'normal',
                    color: aboutTab === tab ? '#8b4513' : '#666',
                    borderBottom: aboutTab === tab ? '3px solid #8b4513' : 'none',
                    marginBottom: '-1rem',
                    paddingBottom: '1.5rem',
                    textTransform: 'capitalize'
                  }}
                >
                  {tab}
                </button>
              ))}
            </div>

            {/* TAB CONTENT */}
            <p style={{
              fontSize: '0.95rem',
              lineHeight: '1.8',
              color: '#555',
              textAlign: 'justify'
            }}>
              {aboutTabs[aboutTab]}
            </p>
          </div>
        </div>
      </div>

      {/* QUICK LINKS SECTION */}
      <div style={{
        background: '#f5f5f5',
        padding: '3rem 0',
        marginTop: '2rem'
      }}>
        <div className="container">
          <h2 style={{ 
            textAlign: 'center', 
            marginBottom: '3rem', 
            color: '#333',
            fontSize: '2rem'
          }}>
